public class DemoIfElse {

	/**
	 * The main method.
	 *
	 * @param args the arguments
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//	if-else
		System.out.println();
		int num = -5;
		if(num == 0) {
			System.out.println("zero");
		} else if(num > 0) {
			System.out.println("positive");
		} else {
			System.out.println("negative");
		}

	}

}
